def floor(instructions: str) -> int:
    """https://adventofcode.com/2015/day/1
    
    Examples:
    >>> floor("(())")
    0
    >>> floor("()()")
    0
    >>> floor("(((")
    3
    >>> floor("(()(()(")
    3
    >>> floor("))(((((")
    3
    >>> floor("())")
    -1
    >>> floor("))(")
    -1
    >>> floor(")))")
    -3
    >>> floor(")())())")
    -3
    """
    floor = 0

    for instruction in instructions:
        if instruction == "(":
            floor += 1
        elif instruction == ")":
            floor -= 1
        else:
            raise ValueError(f"Unknown instruction: {instruction}")
    
    return floor


def entered_basement(instructions: str) -> int:
    """https://adventofcode.com/2015/day/1#part2
    
    >>> entered_basement(")")
    1
    >>> entered_basement("()())")
    5
    """
    floor = 0
    
    for index, instruction in enumerate(instructions, start=1):
        if instruction == "(":
            floor += 1
        elif instruction == ")":
            floor -= 1
        else:
            raise ValueError(f"Unknown instruction: {instruction}")
        
        if floor == -1:
            return index
    
    raise ValueError("Never entered basement")


